import React from "react";

//Material UI
import { Typography, Grid } from "@material-ui/core";

import "./sponsored.styles.sass";

import Background from "../../../../assets/images/backgrounds/wave_3.png";

const sponsored_Celebs = [
  {
    name: "Yousfi Hamza",
    image: "athlete_1.jpg",
  },
  {
    name: "Hamza Yousfi",
    image: "athlete_2.jpg",
  },
];

const Sponsored = () => {
  return (
    <Grid
      container
      justify="space-around"
      style={{
        backgroundImage: "url(" + Background + ")",
        backgroundSize: "100% 17% ",
        backgroundRepeat: "no-repeat",
        backgroundColor: "#00539c",
      }}
    >
      <Grid Item xs={10} md={8} style={{ marginBottom: "0" }}>
        <Typography
          variant="h2"
          align="center"
          style={{
            fontFamily: "lobster",
            marginBottom: "44px",
            textShadow: "5px 5px 10px cyan, 5px 5px 10px blue",
          }}
        >
          Sponsored By YousFitness
        </Typography>
        <Typography
          align="center"
          style={{
            color: "white",
            fontStyle: "italic",
          }}
        >
          Regardless of what an employee’s role is, at some point they will have
          to work with other people at the company – whether that’s a colleague,
          customer or other stakeholder.
          <br />
          Team spirit is an attitude that enables people to work well together.
          It’s about camaraderie, cooperation and collaboration between
          different members of the organization.
        </Typography>
      </Grid>
      <Grid
        container
        xs={11}
        justify="space-around"
        style={{ margin: "55px 0" }}
      >
        {sponsored_Celebs.map((celeb) => {
          const { name, image } = celeb;
          return (
            <Grid
              item
              xs={11}
              md={5}
              style={{
                margin: "55px 0",
                width: "300px",
                height: "600px",
                img: { postion: "absolute" },
              }}
            >
              <div>
                <img
                  src={require(`../../../../assets/images/sponsored/${image}`)}
                  alt="..."
                />
                <img
                  src={require(`../../../../assets/images/sponsored/${image}`)}
                  alt="..."
                />
                <img
                  src={require(`../../../../assets/images/sponsored/${image}`)}
                  alt="..."
                />
                <img
                  src={require(`../../../../assets/images/sponsored/${image}`)}
                  alt="..."
                />
              </div>
            </Grid>
          );
        })}
      </Grid>
    </Grid>
  );
};

export default Sponsored;
