import styled from "styled-components";

import { Typography, Grid } from "@material-ui/core";

import Background from "../../../../assets/images/backgrounds/wave_2.png";

export const MemberContainer = styled(Grid)`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 0 22px;
  margin-top: 44px;
  border-radius: 55px;
  background: "linear-gradient(90deg, rgba(255,255,255,0.33) 50%, rgba(0,212,255,0.66) 75%, rgba(0,9,121,1) 100%)";
`;

export const TeamTitle = styled(Typography)`
  font-family: lobster;
  margin-bottom: 44px;
  text-shadow: 5px 5px 10px cyan, 5px 5px 10px blue;
`;

export const TeamDescription = styled(Typography)`
  color: gray;
  font-style: italic;
`;
