import React from "react";

import StripeCheckout from "react-stripe-checkout";

import Snackbar from "@material-ui/core/Snackbar";
import Alert from "@material-ui/lab/Alert";

import { Redirect } from "react-router-dom";

import Logo from "../../assets/images/YousfitnessLogo.png";

const StripeCheckoutButton = ({ price }) => {
  const priceForStripe = price * 100;
  const publishableKey = "pk_test_9OfuS4gpdp4WRIgXDMroIPo2";

  const [open, setOpen] = React.useState(false);

  const handleClose = (event, reason) => {
    if (reason === "clickaway") {
      return;
    }

    setOpen(false);
  };

  // token for payment processing (backend work needed) we dont need it here for now, this fct is here to show that the payment was successful (or not)
  const onToken = (token) => {
    console.log(token);
    return (
      <Snackbar
        anchorOrigin={{
          vertical: "bottom",
          horizontal: "left",
        }}
        open={open}
        autoHideDuration={10000}
        style={{ zIndex: "999" }}
        action={<Redirect to="./YH-Clothing" />}
      />
    );
  };

  return (
    <StripeCheckout
      label="ðŸ‘‰ðŸ¼ Pay Now ðŸ’³"
      name="Y-H Clothing"
      billingAddress
      shippingAddress
      image={Logo}
      description={`Your Total is : $${price}`}
      amount={priceForStripe}
      panelLabel="Pay Now"
      token={onToken}
      stripeKey={publishableKey}
    />
  );
};

export default StripeCheckoutButton;
