import React from "react";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";

import WithSpinner from "../../components/with-spinner/with-spinner.component";

import CollectionPage from "./collectionPage.component";
import { isCollectionLoaded } from "../../redux/shop/shop.selector";

const CollectionPageWithSpinner = WithSpinner(CollectionPage);

const CollectionPageContainer = ({ isLoaded, ...props }) => (
  <CollectionPageWithSpinner isLoading={!isLoaded} {...props} />
);

const mapStateToProps = createStructuredSelector({
  isLoaded: isCollectionLoaded,
});

export default connect(mapStateToProps)(CollectionPageContainer);
