import styled from "styled-components";

//Material UI
import { Grid } from "@material-ui/core";

export const ContentContainer = styled(Grid)`
  visibility: hidden;
  opacity: 0;
  transition: 1s ease-out;
`;

export const CardContainer = styled(Grid)`
  align-items: center;
  height: 150px;
  &:hover {
    height: 450px;
    ${ContentContainer} {
      visibility: visible;
      opacity: 1;
      transition: 0.5s ease-in;
    }
  }
`;
