import React from "react";

//Material UI
import { Typography, Grid } from "@material-ui/core";

// nodejs library that concatenates classes
import classNames from "classnames";
import { makeStyles } from "@material-ui/core/styles";
import imagesStyles from "../../../../../assets/jss/custom-material-styles/imagesStyles";

import { MemberContainer } from "./teamMember.styles";

const useStyles = makeStyles(imagesStyles);

const TeamMember = ({ membre }) => {
  const classes = useStyles();
  const imageClasses = classNames(
    classes.imgRaised,
    classes.imgRoundedCircle,
    classes.imgFluid
  );

  return (
    <MemberContainer key={membre.id} item xs={10} md={3} data-aos="zoom-in">
      <Grid
        item
        xs={8}
        style={{
          marginTop: "-33px",
        }}
      >
        <img
          alt="..."
          src={require(`../../../../../assets/images/team/${membre.avatar}`)}
          className={imageClasses}
        />
      </Grid>
      <Grid>
        <Grid item xs={10}>
          <Typography
            variant="h4"
            align="center"
            style={{
              fontFamily: "lobster",
              fontSize: "26px",
              fontWeight: "bold",
              color: "darkblue",
            }}
          >
            {membre.name}
            <br />
            <span
              style={{
                color: "Graytext",
              }}
            >
              - {membre.profession.toUpperCase()} -
            </span>
          </Typography>
        </Grid>
        <Grid item xs={10}>
          <Typography
            variant="h6"
            align="center"
            style={{
              fontFamily: "Arial",
              fontSize: "18px",
              color: "gray",
            }}
          >
            {membre.title}
          </Typography>
        </Grid>
        <Grid item xs={12}>
          <Typography
            align="center"
            style={{
              fontStyle: "italic",
              fontFamily: "Grenze Gotisch",
              paddingBottom: "22px",
            }}
          >
            {membre.desc}
          </Typography>
        </Grid>
      </Grid>
    </MemberContainer>
  );
};

export default TeamMember;
