import styled from "styled-components";

export const CheckoutPageContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: white;
  flex-wrap: wrap;
  z-index: 123;
  border-radius: 25px;
  padding: 33px;
  margin-left: 11vw;
  margin-right: 11vw;
  margin-top: -33vh;
  margin-bottom: 66px;
  button {
    width: 25%;
    margin-left: auto;
    margin-top: 50px;
  }
`;

export const CheckoutHeaderContainer = styled.div`
  width: 100%;
  padding: 10px 0;
  display: flex;
  justify-content: space-between;
  border-bottom: 1px solid darkgrey;
  text-align: center;
  &:last-child {
    width: 8%;
  }
`;

export const HeaderBlockContainer = styled.div`
  text-transform: capitalize;
  width: 23%;
  &:first-child {
    text-align: left;
  }
  &:last-child {
    text-align: right;
  }
`;

export const TotalContainer = styled.div`
  margin-top: 30px;
  margin-left: auto;
  font-size: 36px;
`;

export const TestWarningContainer = styled.div`
  text-align: center;
  margin-top: 40px;
  font-size: 24px;
  color: red;
`;
