import styled from "styled-components";

//Material UI
import { Grid } from "@material-ui/core";

export const ContentContainer = styled(Grid)`
  visibility: hidden;
`;

export const CardContainer = styled(Grid)`
  height: 250px;
  &:hover {
    height: 450px;
    ${ContentContainer} {
      visibility: visible;
    }
  }
`;
