import React, { Fragment } from "react";

// nodejs library that concatenates classes
import classNames from "classnames";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

//Material UI
import { Typography, Grid } from "@material-ui/core";

import imagesStyles from "../../assets/jss/custom-material-styles/imagesStyles";

import Parallax from "../../components/parallax/parallax.component";
import InfoArea from "../../components/InfoArea/infoArea.component";

import {
  LocalShippingTwoTone,
  ChatBubbleTwoTone,
  MonetizationOnTwoTone,
} from "@material-ui/icons";

import Directory from "../../components/directory/directory.component";

const useStyles = makeStyles(imagesStyles);

const HomePage = () => {
  const classes = useStyles();
  const imageClasses = classNames(
    classes.imgRaised,
    classes.imgRoundedCircle,
    classes.imgFluid
  );

  return (
    <Fragment>
      <Parallax filter image="home" />

      <Directory />

      <Grid container justify="space-around">
        <Grid Item xs={10} md={6} style={{ marginBottom: "0" }}>
          <Typography
            variant="h1"
            align="center"
            style={{ fontFamily: "lobster", marginBottom: "44px" }}
          >
            The T.E.A.M
          </Typography>
          <Typography
            align="center"
            style={{
              color: "GrayText",
              fontStyle: "italic",
            }}
          >
            Regardless of what an employee’s role is, at some point they will
            have to work with other people at the company – whether that’s a
            colleague, customer or other stakeholder.
            <br />
            Team spirit is an attitude that enables people to work well
            together. It’s about camaraderie, cooperation and collaboration
            between different members of the organization.
          </Typography>
        </Grid>
        <Grid
          container
          xs={11}
          justify="space-around"
          style={{ margin: "55px 0" }}
        >
          <Grid
            item
            xs={10}
            md={3}
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              margin: "22px",
            }}
          >
            <Grid item xs={8}>
              <img
                alt="..."
                src={require("../../assets/images/team/team1.jpg")}
                className={imageClasses}
              />
            </Grid>
            <Grid item xs={10}>
              <Typography
                variant="h4"
                align="center"
                style={{
                  fontFamily: "lobster",
                  fontSize: "26px",
                  fontWeight: "bold",
                }}
              >
                Yousfi Hamza
                <br />- COACH -
              </Typography>
            </Grid>
            <Grid item xs={10}>
              <Typography
                variant="h6"
                align="center"
                style={{ fontFamily: "Arial", fontSize: "18px", color: "gray" }}
              >
                Head of the Sales Team
              </Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography
                align="center"
                style={{
                  fontStyle: "italic",
                  fontFamily: "Grenze Gotisch",
                }}
              >
                Regardless of what an employee’s role is, at some point they
                will have to work with other people at the company – whether
                that’s a colleague, customer or other stakeholder.
                <br />
                Team spirit is an attitude that enables people to work well
                together. It’s about camaraderie, cooperation and collaboration
                between different members of the organization.
              </Typography>
            </Grid>
          </Grid>
          <Grid
            item
            xs={10}
            md={3}
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              margin: "22px",
            }}
          >
            <Grid item md={6} xs={10}>
              <img
                alt="..."
                src={require("../../assets/images/team/team1.jpg")}
                className={imageClasses}
              />
            </Grid>
            <Typography
              variant="h4"
              align="center"
              style={{
                fontFamily: "lobster",
                fontSize: "22px",
                fontWeight: "bold",
              }}
            >
              Yousfi Hamza
            </Typography>
            <Typography
              variant="h6"
              align="center"
              style={{ fontFamily: "Arial", fontSize: "18px", color: "gray" }}
            >
              Coach,
              <br />
              Head of the Sales Team
            </Typography>
            <Typography align="center" style={{ textJustify: "inter-word" }}>
              Regardless of what an employee’s role is, at some point they will
              have to work with other people at the company – whether that’s a
              colleague, customer or other stakeholder.
              <br />
              Team spirit is an attitude that enables people to work well
              together. It’s about camaraderie, cooperation and collaboration
              between different members of the organization.
            </Typography>
          </Grid>
          <Grid
            item
            xs={10}
            md={3}
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              margin: "22px",
            }}
          >
            <Grid item md={6} xs={10}>
              <img
                alt="..."
                src={require("../../assets/images/team/team1.jpg")}
                className={imageClasses}
              />
            </Grid>
            <Typography
              variant="h4"
              align="center"
              style={{
                fontFamily: "lobster",
                fontSize: "22px",
                fontWeight: "bold",
              }}
            >
              Yousfi Hamza
            </Typography>
            <Typography
              variant="h6"
              align="center"
              style={{ fontFamily: "Arial", fontSize: "18px", color: "gray" }}
            >
              Coach,
              <br />
              Head of the Sales Team
            </Typography>
            <Typography align="center" style={{ textJustify: "inter-word" }}>
              Regardless of what an employee’s role is, at some point they will
              have to work with other people at the company – whether that’s a
              colleague, customer or other stakeholder.
              <br />
              Team spirit is an attitude that enables people to work well
              together. It’s about camaraderie, cooperation and collaboration
              between different members of the organization.
            </Typography>
          </Grid>
        </Grid>
      </Grid>
    </Fragment>
  );
};

export default HomePage;
