import styled from "styled-components";

//Material UI
import { Typography, Grid } from "@material-ui/core";

export const ContentContainer = styled.div`
  visibility: hidden;
  display: none;
`;

export const CardContainer = styled(Grid)`
  &:hover {
    ${ContentContainer} {
      visibility: visible;
      display: flex;
    }
  }
`;
