import React from "react";

import {
  CheckoutItemContainer,
  ImageContainer,
  TextContainer,
  QuantityContainer,
  RemoveButton,
} from "./checkout-item.styles";

import { connect } from "react-redux";

import {
  clearItemFromCart,
  addItem,
  removeItem,
} from "../../redux/cart/cart.actions";

const CheckoutItem = ({ item, clearItem, addItem, removeItem }) => {
  const { imageUrl, name, quantity, price } = item;
  return (
    <CheckoutItemContainer container xs={12}>
      <ImageContainer item xs={12} sm={3}>
        <img alt="cart-item" src={imageUrl} />
      </ImageContainer>
      <TextContainer item xs={12} sm={3}>
        {name}
      </TextContainer>
      <QuantityContainer item xs={3}>
        <div className="arrow" onClick={() => removeItem(item)}>
          &#10094;
        </div>
        <span className="value">{quantity}</span>
        <div className="arrow" onClick={() => addItem(item)}>
          &#10095;
        </div>
      </QuantityContainer>
      <TextContainer item xs={3}>
        {price}
      </TextContainer>
      <RemoveButton onClick={() => clearItem(item)}>&#10008;</RemoveButton>
    </CheckoutItemContainer>
  );
};

const mapDispatchToProps = (dispatch) => ({
  clearItem: (item) => dispatch(clearItemFromCart(item)),
  addItem: (item) => dispatch(addItem(item)),
  removeItem: (item) => dispatch(removeItem(item)),
});

export default connect(null, mapDispatchToProps)(CheckoutItem);
