import styled from "styled-components";

//Material UI
import { Grid } from "@material-ui/core";

export const ContentContainer = styled(Grid)`
  visibility: hidden;
  display: none;
`;

export const CardContainer = styled(Grid)`
  &:hover {
    ${ContentContainer} {
      visibility: visible;
      display: block;
    }
  }
`;
