import styled from "styled-components";

import { Grid } from "@material-ui/core";

export const SignInContainer = styled(Grid)`
  width: 30%;
  display: flex;
  flex-direction: column;
`;

export const TitleContainer = styled.h2`
  margin: 10px 0;
`;
