/*eslint-disable*/
import React from "react";

import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";

import { selectCurrentUser } from "../../redux/user/user.selectors";
import {
  selectCartHidden,
  selectCartItems,
} from "../../redux/cart/cart.selectors";

import { auth } from "../../firebase/firebase.utils.js";

import DeleteIcon from "@material-ui/icons/Delete";
import IconButton from "@material-ui/core/IconButton";
// react components for routing our app without refresh
import { Link } from "react-router-dom";

// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import Tooltip from "@material-ui/core/Tooltip";
import Icon from "@material-ui/core/Icon";

// @material-ui/icons
import {
  Apps,
  CloudDownload,
  StorefrontTwoTone,
  ContactMailTwoTone,
} from "@material-ui/icons";

// core components
import CustomDropdown from "../customDropdown/customDropdown.component";
import Button from "../customButton2/button.component";

import styles from "./headerLinks.styles";

const useStyles = makeStyles(styles);

const HeaderLinks = ({ currentUser, hidden, cartItems }) => {
  const classes = useStyles();
  return (
    <List className={classes.list}>
      <ListItem className={classes.listItem}>
        <Button
          href="https://www.creative-tim.com/product/material-kit-react?ref=mkr-navbar"
          color="transparent"
          target="_blank"
          className={classes.navLink}
        >
          <CloudDownload className={classes.icons} /> Download
        </Button>
      </ListItem>

      <ListItem className={classes.listItem}>
        <Tooltip
          title="But it's probably closed now ..."
          placement={window.innerWidth > 959 ? "top" : "left"}
          classes={{ tooltip: classes.tooltip }}
        >
          <Link to="/shop" className={classes.navLink}>
            <StorefrontTwoTone className={classes.icons} /> Shop
          </Link>
        </Tooltip>
      </ListItem>

      <ListItem className={classes.listItem}>
        <Tooltip
          id="instagram-twitter"
          title="Or Dont ..."
          placement={window.innerWidth > 959 ? "top" : "left"}
          classes={{ tooltip: classes.tooltip }}
        >
          <Link to="/contact" className={classes.navLink}>
            <ContactMailTwoToneIcon className={classes.icons} /> Contact Us
          </Link>
        </Tooltip>
      </ListItem>
    </List>
  );
};

const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
  hidden: selectCartHidden,
  cartItems: selectCartItems,
});

export default connect(mapStateToProps)(HeaderLinks);
